let userList = [{
	username: '', //用户账号
	password: '', //用户密码
	step: 0,//要刷的步数，0为随机
},{
	username: '', //用户账号2
	password: '', //用户密码2
	step: 0,//要刷的步数，0为随机
},{
	username: '', //用户账号3
	password: '', //用户密码3
	step: 0,//要刷的步数，0为随机
}];//依次内推  ，多账号加个,{username: '',password: '',step: 0}在后面就行了
main();
async function main(){
    let count=0;
    for(let item of userList){
        count++;
        if(item.username&&item.password){
            await loginByPassword(item.username,item.password);
    	if($.token_info){
    	    await get_app_token($.token_info.login_token);
    	    if($.tokenInfo){
    	        $.app_token=$.tokenInfo.token_info.app_token;
    	        await change_step($.app_token,$.token_info.user_id,item.step);
    	    }else{
    	        that.log("获取app_token失败");
    	    }
    	}
        }else{
            if(count==1){
                that.log("你他妈的没填账号密码");
            }
    	    }
    	
    }
}

function change_step(app_token, user_id,stepx=0) {
    let STEP = Math.ceil(Math.random() * 10000) + 20000;
    if(stepx!=0){
        STEP=stepx;
    }
    let dateObj = new Date();
    let dataJson2=[
  {
    "summary": "{\"slp\":{\"ss\":73,\"lt\":304,\"dt\":0,\"st\":1589920140,\"lb\":36,\"dp\":92,\"is\":208,\"rhr\":0,\"stage\":[{\"start\":269,\"stop\":357,\"mode\":2},{\"start\":358,\"stop\":380,\"mode\":3},{\"start\":381,\"stop\":407,\"mode\":2},{\"start\":408,\"stop\":423,\"mode\":3},{\"start\":424,\"stop\":488,\"mode\":2},{\"start\":489,\"stop\":502,\"mode\":3},{\"start\":503,\"stop\":512,\"mode\":2},{\"start\":513,\"stop\":522,\"mode\":3},{\"start\":523,\"stop\":568,\"mode\":2},{\"start\":569,\"stop\":581,\"mode\":3},{\"start\":582,\"stop\":638,\"mode\":2},{\"start\":639,\"stop\":654,\"mode\":3},{\"start\":655,\"stop\":665,\"mode\":2}],\"ed\":1589943900,\"wk\":0,\"wc\":0},\"tz\":\"28800\",\"stp\":{\"runCal\":1,\"cal\":6,\"conAct\":0,\"stage\":[],\"ttl\":"+STEP+",\"dis\":144,\"rn\":0,\"wk\":5,\"runDist\":4,\"ncal\":0},\"v\":5,\"goal\":8000}",
    "data": [
        {
            "stop": 1439,
            "value": "",
            "did": "DA932FFFFE8816E7",
            "tz": 32,
            "src": 17,
            "start": 0
        }
    ],
    "data_hr": "",
    "summary_hr": "{\"ct\":0,\"id\":[]}",
    "date": `${dateObj.getFullYear()}-${dateObj.getMonth() + 1}-${dateObj.getDate()}`
  }
]
	let dataJSON=JSON.stringify(dataJson2);
  return new Promise(resolve => {
    const options = {
      "url": `https://api-mifit-cn2.huami.com/v1/data/band_data.json?&t=${$.t}`,
      "body": `userid=${user_id}&last_sync_data_time=1597306380&device_type=0&last_deviceid=DA932FFFFE8816E7&data_json=${dataJSON}`,
      "headers": {
        "Content-Type":"application/x-www-form-urlencoded;charset=UTF-8",
        "apptoken": app_token,
      }
    }
    $.post(options, (err, resp, data) => {
      try {
        if (err) {
          that.log(`${JSON.stringify(err)}`)
          that.log(`${$.name} API请求失败，请检查网路重试`)
        } else {
          that.log(`修改步数结果:${data}`);
          $.changeStepRes = JSON.parse(data);
           that.log(`改变步数为 ${STEP}, 状态： ${$.changeStepRes.message}`)
        }
      } catch (e) {
        $.logErr(e, resp)
      } finally {
        resolve();
      }
    })
  })
}


function get_app_token(login_token){
    return new Promise(resolve => {
    $.get({url: `https://account-cn.huami.com/v1/client/app_tokens?app_name=com.xiaomi.hm.health&dn=api-user.huami.com%2Capi-mifit.huami.com%2Capp-analytics.huami.com&login_token=${login_token}&os_version=4.1.0`}, (err, resp, data) => {
      try {
        if (err) {
          that.log(`${JSON.stringify(err)}`)
          that.log(`${$.name} API请求失败，请检查网路重试`)
        } else {
          if (data) {
            $.tokenInfo = JSON.parse(data);
          }
        }
      } catch (e) {
        $.logErr(e, resp)
      } finally {
        resolve();
      }
    })
  })
}


function loginByPassword(username, password) {
	return new Promise(resolve => {
		let body = {
			client_id: "HuaMi",
			password: password,
			redirect_uri: 'https://tyh52.com/jd/step.php',
			token: "access",
		};
		$.post(taskUrl(`https://api-user.huami.com/registrations/+86${username}/tokens`, body),
			async (err, resp, data) => {
				try {
					if (err) {
						that.log(`${err}`)
						that.log(`${$.name} API请求失败，请检查网路重试`)
					} else {
					    data=JSON.parse(data);
					    if(data){
    					    $.code=data.access;
    					    if($.code){
    					        that.log("获取code成功");
    					        await login($.code);
    					    }else{
    					        that.log("获取code失败，请检查账号密码");
    					    }
					    }
					}
				} catch (e) {
					$.logErr(e, resp)
				} finally {
					resolve(data);
				}
			})
	});
}

function login(access) {
	return new Promise(resolve => {
		let body =  {
      "app_name":"com.xiaomi.hm.health",
      "app_version":"4.6.0",
      "code": access,
      "country_code":"CN",
      "device_id":"2C8B4939-0CCD-4E94-8CBA-CB8EA6E613A1",
      "device_model":"phone",
      "grant_type":"access_token",
      "third_name":"huami_phone",
    };
		$.post(taskUrl('https://account.huami.com/v2/client/login', body),
			async (err, resp, data) => {
			    that.log(err, resp, data);
				try {
					if (err) {
						that.log(`${err}`)
						that.log(`${$.name} API请求失败，请检查网路重试`)
					} else {
					    data=JSON.parse(data);
					   if(data){
					        $.token_info=data.token_info;
					        if($.token_info){
					            that.log("登录成功");
					        }else{
					            that.log("登录失败");
					        }
					   }else{
					            that.log("登录失败");
					        }
					}
				} catch (e) {
					$.logErr(e, resp)
				} finally {
					resolve(data);
				}
			})
	});
}


function taskUrl(url, body = '') {
	return {
		url: url,
		body: body,
		headers: {
			"Content-Type": "application/x-www-form-urlencoded;charset=UTF-8",
			"User-Agent": "MiFit/4.6.0 (iPhone; iOS 14.0.1; Scale/2.00)",
		}
	}
}

function get_time() {
  return new Promise(resolve => {
    $.get({url: `http://api.m.taobao.com/rest/api3.do?api=mtop.common.getTimestamp`}, (err, resp, data) => {
      try {
        if (err) {
          that.log(`${JSON.stringify(err)}`)
          that.log(`${$.name} API请求失败，请检查网路重试`)
        } else {
          if (data) {
            data = JSON.parse(data);
            $.t = data.data.t;
          }
        }
      } catch (e) {
        $.logErr(e, resp)
      } finally {
        resolve(data);
      }
    })
  })
}
